import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données des personnages:', error);
        setLoading(false);
      });
  }, [name]);

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!character) {
    return <div>Personnage non trouvé.</div>;
  }

  return (
    <div className="character-container">
      <div className="character-description-box">
        <h1>Personnage : {character.name}</h1>
        <img src={character.image || 'fallback-image-url.jpg'} alt={character.name} />
        <p>{character.description}</p>
        <p className="details">{character.details}</p>
      </div>
    </div>
  );
}

export default Character;
