// Secret.js
import React, { useEffect, useState } from "react";
import { useSecret } from './SecretContext';

function Secret() {
  const { secret, revealSecret } = useSecret();
  const [isRevealed, setIsRevealed] = useState(secret.found);
  useEffect(() => {
    setIsRevealed(secret.found);
  }, [secret]);
  const handleReveal = () => {
    revealSecret(1);
  };

  return (
    <div>
      <h1>Page des Secrets</h1>
      <p>Voici le secret :</p>
      <p>{secret.text}</p>
      <p>État du Secret : {isRevealed ? "Révélé" : "Non Révélé"}</p>
      <button onClick={handleReveal}>Révéler le Secret</button>
    </div>
  );
}

export default Secret;
