// SecretContext.js
import React, { createContext, useState, useContext } from 'react';

const SecretContext = createContext();

export const SecretProvider = ({ children }) => {
  const [revelSecret, setRevelSecret] = useState(false);

  return (
    <SecretContext.Provider value={{ revelSecret, setRevelSecret }}>
      {children}
    </SecretContext.Provider>
  );
};

export const useSecret = () => {
  const context = useContext(SecretContext);
  if (!context) {
    throw new Error('useSecret must be used within a SecretProvider');
  }
  return context;
};
