import React, { useState, useEffect } from "react";
import "../style/main.css";
import hateSpeech from "../ressource/AM_hate_speech.mp3";
import hateSpeech_2 from "../ressource/HateSpeech_2.mp3"
import CarouselMenue from "./Carousel";

import { useSecret } from "./SecretContext";

function Main() {
  const [data, setData] = useState({});
  const { revelSecret } = useSecret();

  useEffect(() => {
    console.log("Fetching data...");
    fetch("http://localhost:5000/api/Home")
      .then(response => response.json())
      .then(data => {
        console.log("Data fetched:", data);
        setData(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Déterminez la classe CSS conditionnelle pour madness.css
  const madnessClass = revelSecret ? 'madness-mode' : '';

  const quoteText = data.quote ? data.quote.text : "";
  const quoteAuthor = data.quote ? data.quote.author : "";

  return (
    <div className={`main-container ${madnessClass}`}>
      <div className="description-box">
        <div className="top-content">
          <div className="text-content">
            <h1>{data.title}</h1>
            <p>{data.description}</p>
          </div>
          <div className="cover-container" />
        </div>

        <div className="bottom-content">
          {quoteText && quoteAuthor && (
            <>
              <blockquote>
                <span className="citation">{quoteText}</span><br />— {quoteAuthor}
              </blockquote>
              <audio controls>
                {!revelSecret ? (
                  <source src={hateSpeech} type="audio/mp3" />
                ) : (
                  <source src={hateSpeech_2} type="audio/mp3" />
                )}
                Votre navigateur ne supporte pas l'élément audio.
              </audio>
            </>
          )}

          <div className="characters">
            <h2>Personnages principaux :</h2>
            <CarouselMenue />
          </div>

          <h2>Thèmes abordés :</h2>
          <ul>
            {data.themes && data.themes.map((theme, index) => (
              <li key={index}>{theme}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Main;
