// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Main from './components/Main';
import Histoire from './components/Histoire';
import Character from './components/Character';
import NotFound from './components/NotFound';
import { SecretProvider, useSecret } from './components/SecretContext';
import './style/menu.css';
import './style/404Error.css';
import './style/parasiteEffect.css'; // Importer le fichier CSS pour l'effet de parasite
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <SecretProvider>
      <Router>
        <div className="App bg-dark text-light">
          <header>
            <Header />
          </header>
          <main className="main" style={{ paddingTop: '80px' }}>
            <Routes>
              <Route path="/" element={<Main />} />
              <Route path="/story" element={<Histoire />} />
              <Route path="/character/:name" element={<Character />} />
              <Route path="*" element={<NotFound />} />
            </Routes>
            <ParasiteEffect /> {/* Ajouter le composant d'effet de parasite */}
          </main>
        </div>
      </Router>
    </SecretProvider>
  );
}

function ParasiteEffect() {
  const { revelSecret } = useSecret();

  return revelSecret ? <div className="parasite-effect"></div> : null;
}

export default App;
