import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../style/navbar.css";
import useSound from "use-sound";

import gorristerYell from "../sounds/gorrister.mp3";
import bennyYell from "../sounds/benny.mp3";
import tedYell from "../sounds/ted.mp3";
import ellenYell from "../sounds/ellen.mp3";
import nimdok from "../sounds/nimdok.mp3";
import cogitoErgoSum from "../sounds/CogitoErgoSum.mp3";

function Header() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [canPlay, setCanPlAY] = useState(true);

  const [playAM] = useSound(cogitoErgoSum, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const [playNimdok] = useSound(nimdok, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const [playEllenYell] = useSound(ellenYell, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const [playGorrister] = useSound(gorristerYell, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const [playBennyYell] = useSound(bennyYell, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const [playTedYell] = useSound(tedYell, {
    onend: () => setIsPlaying(false),
    interrupt: true,
  });

  const handleMouseOver = (characterName) => {
    if (!isPlaying && canPlay) {
      setIsPlaying(true);
      switch (characterName) {
        case "Ellen":
          playEllenYell();
          break;
        case "Benny":
          playBennyYell();
          break;
        case "Gorrister":
          playGorrister();
          break;
        case "Ted":
          playTedYell();
          break;
        case "Nimdok":
          playNimdok();
          break;
        case "AM":
          playAM();
          break;
        default:
          break;
      }
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav className={`navbar navbar-expand-lg navbar-dark bg-dark ${scrolled ? "navbar-scrolled" : ""}`}>
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <img src={process.env.PUBLIC_URL + "/favico.ico"} alt="" width="30" height="24" />
          IHNMAIMS
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">
                Acceuil
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/story">
                Histoire
              </Link>
            </li>
            <li className="nav-item dropdown">
              <Link
                className="nav-link dropdown-toggle"
                to="#"
                id="navbarDropdownMenuLink"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                Personnage
              </Link>
              <ul className="dropdown-menu" aria-labelledby="navbarScrollingDropdown">
                <li>
                  <span className="h5">Antagoniste</span>
                </li>
                <li>
                  <Link
                    to="/character/AM"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("AM")}
                  >
                    A.M
                  </Link>
                </li>
                <li>
                  <div className="dropdown-divider" />
                </li>
                <li>
                  <span className="h5">Protagoniste</span>
                </li>
                <li>
                  <Link
                    to="/character/Ted"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("Ted")}
                  >
                    Ted
                  </Link>
                </li>
                <li>
                  <Link
                    to="/character/Ellen"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("Ellen")}
                  >
                    Ellen
                  </Link>
                </li>
                <li>
                  <Link
                    to="/character/Nimdok"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("Nimdok")}
                  >
                    Nimdok (Maldonado)
                  </Link>
                </li>
                <li>
                  <Link
                    to="/character/Benny"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("Benny")}
                  >
                    Benny
                  </Link>
                </li>
                <li>
                  <Link
                    to="/character/Gorrister"
                    className="dropdown-item"
                    onMouseOver={() => handleMouseOver("Gorrister")}
                  >
                    Gorrister
                  </Link>
                </li>
              </ul>
            </li>
            <li className="nav-item">
                  <label class="switch">
                    <input type="checkbox" />
                    <span class="slider round"></span>
                  </label>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Header;
