import React, { useState, useEffect } from "react";
import "../style/main.css";

function Main() {
  const [data, setData] = useState({});

  useEffect(() => {
    fetch("http://localhost:5000/api/Home")
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Vérifier si data.quote est défini avant de l'utiliser
  const quoteText = data.quote ? data.quote.text : "";
  const quoteAuthor = data.quote ? data.quote.author : "";

  return (
    <div className="main-container">
      <div className="cover-container">
        {data.coverUrl && <img src={process.env.PUBLIC_URL + data.coverUrl} alt="Cover" />}
      </div>
      <div className="description-box">
        <h1>{data.title}</h1>
        <p>{data.description}</p>
        
        {quoteText && quoteAuthor && (
          <blockquote><span className="citation">{quoteText}</span><br />— {quoteAuthor}</blockquote>
        )}
        
        <div className="characters">
          <h2>Personnages principaux :</h2>
          <ul>
            {data.characters && data.characters.map((character, index) => (
              <li key={index}>
                <strong>{character.name}</strong>: {character.description}
              </li>
            ))}
          </ul>
        </div>

        <h2>Thèmes abordés :</h2>
        <ul>
          {data.themes && data.themes.map((theme, index) => (
            <li key={index}>{theme}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Main;
