// SecretContext.js
import React, { createContext, useState, useContext } from 'react';

const SecretContext = createContext();

export const SecretProvider = ({ children }) => {
  const [secrets, setSecrets] = useState({
    revelSecret: { value: false, description: 'Description of revelSecret' },
  });

  const setSecret = (key, value, description) => {
    setSecrets(prevSecrets => ({
      ...prevSecrets,
      [key]: { value, description }
    }));
  };

  const getSecret = (key) => {
    return secrets[key] ? secrets[key].value : null;
  };

  const getSecretDescription = (key) => {
    return secrets[key] ? secrets[key].description : '';
  };

  return (
    <SecretContext.Provider value={{ setSecret, getSecret, getSecretDescription }}>
      {children}
    </SecretContext.Provider>
  );
};

export const useSecret = () => {
  const context = useContext(SecretContext);
  if (!context) {
    throw new Error('useSecret must be used within a SecretProvider');
  }
  return context;
};
