import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../style/navbar.css";
import useSound from "use-sound";
import { useSecret } from './SecretContext';
import sounds from "./sounds"; // Importer les sons de manière centralisée

function Header() {
  const { getSecret } = useSecret();
  const [isPlaying, setIsPlaying] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [canPlay, setCanPlay] = useState(true);

  const [playCogitoErgoSum] = useSound(sounds.cogitoErgoSum);
  const [playTrueCogitoErgoSum] = useSound(sounds.true_cogitoErogSum);
  const [playNimdok] = useSound(sounds.nimdok);
  const [playEllenYell] = useSound(sounds.ellenYell);
  const [playGorristerYell] = useSound(sounds.gorristerYell);
  const [playBennyYell] = useSound(sounds.bennyYell);
  const [playTedYell] = useSound(sounds.tedYell);

  const handleMouseOver = (characterName) => {
    if (canPlay) {
      setIsPlaying(true);
      switch (characterName) {
        case 'AM':
          if (getSecret('amSecret')) {
            playTrueCogitoErgoSum();
          } else {
            playCogitoErgoSum();
          }
          break;
        case 'Nimdok':
          playNimdok();
          break;
        case 'Ellen':
          playEllenYell();
          break;
        case 'Gorrister':
          playGorristerYell();
          break;
        case 'Benny':
          playBennyYell();
          break;
        case 'Ted':
          playTedYell();
          break;
        default:
          break;
      }
    }
  };

  const handleMouseOut = () => {
    setIsPlaying(false);
  };

  const handleScroll = () => {
    setScrolled(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleToggle = () => {
    setCanPlay(prevCanPlay => !prevCanPlay);
  };

  return (
    <nav className={`navbar navbar-expand-lg navbar-dark ${scrolled ? "navbar-scrolled" : ""}`}>
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <img src={`${process.env.PUBLIC_URL}/favico.ico`} alt="" width="30" height="24" />
          IHNMAIMS
        </Link>
        <div className="collapse navbar-collapse show" id="navbarSupportedContent">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">Accueil</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/story">Histoire</Link>
            </li>
            <li className="nav-item dropdown">
              <Link className="nav-link dropdown-toggle" to="#" id="navbarDropdownMenuLink" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Personnages
              </Link>
              <ul className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                <li><span className="h5">Antagoniste</span></li>
                <li>
                  <Link to="/character/AM" className="dropdown-item" onMouseOver={() => handleMouseOver("AM")} onMouseOut={handleMouseOut}>
                    A.M
                  </Link>
                </li>
                <li><div className="dropdown-divider" /></li>
                <li><span className="h5">Protagonistes</span></li>
                <CharacterLink name="Ted" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
                <CharacterLink name="Ellen" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
                <CharacterLink name="Nimdok" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
                <CharacterLink name="Benny" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
                <CharacterLink name="Gorrister" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
              </ul>
            </li>
            <li className="nav-item">
              <label className="switch" title={canPlay ? "Son Activé" : "Son Désactivé"}>
                <input type="checkbox" checked={canPlay} onChange={handleToggle} />
                <span className="slider round"></span>
              </label>
            </li>
            {getSecret("pageSecret") && (
              <li className="nav-item nav-link-glitch">
                <Link className="nav-link" to="/Secret">Secret</Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}

const CharacterLink = ({ name, onMouseOver, onMouseOut }) => (
  <li>
    <Link
      to={`/character/${name}`}
      className="dropdown-item"
      onMouseOver={() => onMouseOver(name)}
      onMouseOut={onMouseOut}
    >
      {name}
    </Link>
  </li>
);

export default Header;
