import React, { useState, useEffect } from "react";
import '../style/main.css';

function Histoire() {
  const [histoire, setHistoire] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/histoire')
      .then(response => response.json())
      .then(data => {
        setHistoire(data.histoire)
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données d\'histoire:', error);
      });
  }, []);

  return (
    <div className="main-container">
      <div className="description-box">
        <h1>Histoire</h1>
        {histoire && (
          <>
            <p>{histoire.introduction.contenu}</p>
            <div className="top-content">
              <div className="text-content">
                <h2>{histoire.partie1.titre}</h2>
                <p>{histoire.partie1.chapitre1.contenu}</p>
              </div>
            </div>
            <div className="bottom-content">
              <div className="characters">
                <h2>Personnages</h2>
                <ul>
                  <li>{histoire.partie2.chapitre1.contenu}</li>
                </ul>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Histoire;
