import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { revelSecret, setRevelSecret } = useSecret();

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données des personnages:', error);
        setLoading(false);
      });
  }, [name]);

  const handleMouseOver = () => {
    // Révéler la lettre correspondante à chaque personnage selon la séquence "HATES"
    switch (name) {
      case "Ted":
        setRevealedLetter('H');
        break;
      case "Ellen":
        setRevealedLetter('A');
        break;
      case "Nimdok":
        setRevealedLetter('T');
        break;
      case "Benny":
        setRevealedLetter('E');
        break;
      case "Gorrister":
        setRevealedLetter('S');
        break;
      default:
        setRevealedLetter('');
        break;
    }
  };

  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();
    
    // Vérifier si le personnage est AM avant de continuer
    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      // Mettre à jour displayedSequence si la touche correspond à la prochaine lettre de la séquence "HATES"
      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        // Réinitialiser displayedSequence si la touche ne correspond pas à la prochaine lettre attendue
        setDisplayedSequence('');
      }
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [character, displayedSequence]);

  useEffect(() => {
    if (typedSequence.endsWith(hateSequence) && character && character.name === "AM") {
      // Action lorsque la séquence "HATES" est entièrement tapée
      playHateSpeech();
      setRevelSecret(true);
    }
  }, [typedSequence, character]);

  const playHateSpeech = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!character) {
    return <div>Personnage non trouvé.</div>;
  }

  return (
    <div className="character-container">
      <div className="character-description-box">
        <h1>Personnage : {character.name}</h1>
        <img 
          src={character.image || 'fallback-image-url.jpg'} 
          alt={character.name} 
          onMouseOver={handleMouseOver}
          onMouseOut={handleMouseOut}
        />
        {revealedLetter && (
          <div className="hint">
            Indice : {revealedLetter}
          </div>
        )}
        <p>{character.description}</p>
        <h2>Livre :</h2>
        <div className="details">
          <h3>Personnalité :</h3>
          <p>{character.details.personality.description}</p>
          {character.name === "AM" && (
            <div className="am-details">
            <ul>
              <li>
                <strong>Général</strong> : {character.details.personality.general.description}
              </li>
              <li>
                <strong>Ego</strong> : {character.details.personality.ego.description}
              </li>  
              <li>
                <strong>Super ego</strong> : {character.details.personality.superEgo.description}
              </li>
              <li>
                <strong>ID</strong> : {character.details.personality.id.description}
              </li>
            </ul>
            </div>
          )}
          {character.details.personality.details && (
            <p>{character.details.personality.details}</p>
          )}
          
          <h3>Background :</h3>
          <p>{character.details.background.story}</p>
          <h3>Acts :</h3>
          <ul>
            {character.details.background.acts.map((act, index) => (
              <li key={index}>
                <strong>{act.title}</strong>: {act.description}
              </li>
            ))}
          </ul>

          {character.details.relationships && (
            <div>
              <h3>Relations :</h3>
              <ul>
                {Object.keys(character.details.relationships).map((key, index) => (
                  <li key={index}>
                    <strong>{key}:</strong> {character.details.relationships[key]}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {character.details.tortures && (
            <div>
              <h3>Tortures :</h3>
              <p>{character.details.tortures.description}</p>
            </div>
          )}

          {character.details.evolution && (
            <div>
              <h3>Évolution :</h3>
              <p>{character.details.evolution.description}</p>
              <p>{character.details.evolution.finality}</p>
            </div>
          )}

          {character.name === "AM" && (
            <div className="am-details">
              <h3>Motivations :</h3>
              <ul>
                {character.details.motivations.map((motivation, index) => (
                  <li key={index}>{motivation.description}</li>
                ))}
              </ul>

              <h3>Actions :</h3>
              <ul>
                {character.details.actions.map((action, index) => (
                  <li key={index}>
                    <strong>{action.title}</strong>: {action.description}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {character.name === "AM" && (
            <p>Séquence saisie : {displayedSequence}</p>
          )}
        </div>
      </div>

      <audio ref={audioRef} src={amReason} />
    </div>
  );
}

export default Character;
