import React, { createContext, useState, useEffect, useContext } from 'react';

const SecretContext = createContext();

const getStoredSecrets = () => {
  const secrets = localStorage.getItem('secrets');
  return secrets ? JSON.parse(secrets) : {};
};

const getStoredDescriptions = () => {
  const descriptions = localStorage.getItem('descriptions');
  return descriptions ? JSON.parse(descriptions) : {};
};

export const SecretProvider = ({ children }) => {
  const [secrets, setSecrets] = useState(getStoredSecrets);
  const [descriptions, setDescriptions] = useState(getStoredDescriptions);

  useEffect(() => {
    localStorage.setItem('secrets', JSON.stringify(secrets));
  }, [secrets]);

  useEffect(() => {
    localStorage.setItem('descriptions', JSON.stringify(descriptions));
  }, [descriptions]);

  const setSecret = (key, value) => {
    setSecrets(prevSecrets => ({ ...prevSecrets, [key]: value }));
  };

  const getSecret = (key) => {
    return secrets[key];
  };

  const setDescription = (key, value) => {
    setDescriptions(prevDescriptions => ({ ...prevDescriptions, [key]: value }));
  };

  const getDescription = (key) => {
    return descriptions[key];
  };

  return (
    <SecretContext.Provider value={{ setSecret, getSecret, setDescription, getDescription }}>
      {children}
    </SecretContext.Provider>
  );
};

export const useSecret = () => {
  const context = useContext(SecretContext);
  if (!context) {
    throw new Error('useSecret must be used within a SecretProvider');
  }
  return context;
};
