import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Nav, NavDropdown, Form } from 'react-bootstrap';
import { useSoundContext } from './SoundContext';
import { useSecret } from './SecretContext';
import './navbar.css'; // Importer le CSS

const NavbarComponent = () => {
  const { getSecret } = useSecret();
  const { playSound, stopSound } = useSoundContext();
  const [scrolled, setScrolled] = useState(false);
  const [canPlay, setCanPlay] = useState(true);

  const handleMouseOver = useCallback((characterName) => {
    if (canPlay) {
      const soundMap = {
        AM: getSecret('amSecret') ? 'true_cogitoErogSum' : 'cogitoErgoSum',
        Nimdok: 'nimdok',
        Ellen: 'ellenYell',
        Gorrister: 'gorristerYell',
        Benny: 'bennyYell',
        Ted: 'tedYell',
      };
      playSound(soundMap[characterName]);
    }
  }, [canPlay, getSecret, playSound]);

  const handleMouseOut = useCallback(() => {
    stopSound();
  }, [stopSound]);

  const handleScroll = () => {
    setScrolled(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleToggle = () => {
    setCanPlay((prevCanPlay) => !prevCanPlay);
  };

  return (
    <Navbar expand="lg" className={`navbar ${scrolled ? 'navbar-scrolled' : ''}`}>
      <Navbar.Brand as={Link} to="/">
        <img src={`${process.env.PUBLIC_URL}/favico.ico`} alt="" width="30" height="24" />
        IHNMAIMS
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarSupportedContent" />
      <Navbar.Collapse id="navbarSupportedContent">
        <Nav className="mr-auto">
          <Nav.Link as={Link} to="/">Accueil</Nav.Link>
          <Nav.Link as={Link} to="/story">Histoire</Nav.Link>
          <NavDropdown title="Personnages" id="navbarDropdownMenuLink">
            <NavDropdown.ItemText>Antagoniste</NavDropdown.ItemText>
            <NavDropdown.Item as={Link} to="/character/AM" onMouseOver={() => handleMouseOver('AM')} onMouseOut={handleMouseOut}>
              A.M
            </NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.ItemText>Protagonistes</NavDropdown.ItemText>
            {['Ted', 'Ellen', 'Nimdok', 'Benny', 'Gorrister'].map(name => (
              <NavDropdown.Item
                as={Link}
                to={`/character/${name}`}
                key={name}
                onMouseOver={() => handleMouseOver(name)}
                onMouseOut={handleMouseOut}
              >
                {name}
              </NavDropdown.Item>
            ))}
          </NavDropdown>
          <Form inline>
            <Form.Check
              type="switch"
              id="custom-switch"
              label={canPlay ? 'Son Activé' : 'Son Désactivé'}
              checked={canPlay}
              onChange={handleToggle}
            />
          </Form>
          {getSecret('pageSecret') && (
            <Nav.Link as={Link} to="/Secret" className="nav-link-glitch">
              Secret
            </Nav.Link>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default NavbarComponent;
