import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { revelSecret, setRevelSecret } = useSecret();

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données des personnages :', error);
        setLoading(false);
      });
  }, [name]);

  const handleMouseOver = () => {
    switch (name) {
      case "Ted":
        setRevealedLetter('H');
        break;
      case "Ellen":
        setRevealedLetter('A');
        break;
      case "Nimdok":
        setRevealedLetter('T');
        break;
      case "Benny":
        setRevealedLetter('E');
        break;
      case "Gorrister":
        setRevealedLetter('S');
        break;
      default:
        setRevealedLetter('');
        break;
    }
  };

  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();

    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        setDisplayedSequence('');
      }
    }
  };

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [handleKeyDown]);

  useEffect(() => {
    audioRef.current.play();
    audioRef.current.addEventListener('ended', () => setRevelSecret(true));
    return () => {
      audioRef.current.removeEventListener('ended', () => setRevelSecret(true));
    };
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="character-container" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
      <h1>{character.name}</h1>
      <img src={character.image} alt={`${character.name}`} />
      <p>{character.description}</p>
      <audio ref={audioRef} src={amReason} />
      {revealedLetter && <p>{`Letter revealed: ${revealedLetter}`}</p>}
      {character.details && (
        <div>
          <h2>Character Details</h2>
          <p>{character.details.personality.description}</p>
          <p>{character.details.personality.details}</p>
          <p>{character.details.background.story}</p>
          {character.details.relationships && (
            <div>
              <h3>Relationships</h3>
              <ul>
                {Object.keys(character.details.relationships).map((key) => (
                  <li key={key}>
                    <strong>{key}: </strong>
                    {character.details.relationships[key]}
                  </li>
                ))}
              </ul>
            </div>
          )}
          {character.details.tortures && (
            <div>
              <h3>Tortures</h3>
              <p>{character.details.tortures.description}</p>
            </div>
          )}
          {character.details.evolution && (
            <div>
              <h3>Evolution</h3>
              <p>{character.details.evolution.description}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Character;
