import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import Main from "./components/Main";
import Histoire from "./components/Histoire";
import Character from "./components/Character";

import "./style/menu.css";
import "./style/404Error.css";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <Router>
      <div className="App">
        <div className="overlay"></div> {/* Overlay pour le fond noir */}
        <div className="bg-dark text-light"> {/* Contenu principal avec arrière-plan sombre */}
          <header>
            <Header />
          </header>
          <main className="main" style={{ paddingTop: '80px' }}>
            <Routes>
              <Route path="/" element={<Main />} />
              <Route path="/story" element={<Histoire />} />
              <Route path="/character/:name" element={<Character />} />
              {/* Route pour gérer les erreurs 404 */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

// Composant pour l'erreur 404
function NotFound() {
  return (
    <div className="error-container">
      <h1>Erreur 404 - Page non trouvée</h1>
      <p>La page que vous recherchez n'existe pas.</p>
      {/* Vous pouvez ajouter un lien de retour à la page d'accueil par exemple */}
      {/* <Link to="/">Retour à l'accueil</Link> */}
    </div>
  );
}

export default App;
