import React, { useState, useEffect } from "react";
import axios from "axios";
import '../style/histoire.css'

function Histoire() {
  const [histoireData, setHistoireData] = useState(null);

  useEffect(() => {
    fetchHistoireData();
  }, []);

  const fetchHistoireData = () => {
    axios.get('/api/histoire')
      .then(response => {
        setHistoireData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handleSave = () => {
    axios.post('http://localhost:5000/api/histoire', histoireData)
      .then(response => {
        console.log('Data saved successfully:', response.data);
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  const handleInputChange = (event, section, key) => {
    const updatedData = { ...histoireData };
    updatedData[section][key] = event.target.value;
    setHistoireData(updatedData);
  };

  if (!histoireData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="main-container">
      <div className="description-box">
        <h1>Histoire</h1>
        <form>
          <div className="section">
            <h2>{histoireData.introduction.titre}</h2>
            <textarea
              value={histoireData.introduction.contenu}
              onChange={(e) => handleInputChange(e, 'introduction', 'contenu')}
              rows="4"
              cols="50"
            />
          </div>

          <div className="section">
            <h2>{histoireData.partie1.titre}</h2>
            <div className="chapter">
              <h3>{histoireData.partie1.chapitre1.titre}</h3>
              <textarea
                value={histoireData.partie1.chapitre1.contenu}
                onChange={(e) => handleInputChange(e, 'partie1', 'chapitre1.contenu')}
                rows="4"
                cols="50"
              />
            </div>
            <div className="chapter">
              <h3>{histoireData.partie1.chapitre2.titre}</h3>
              <textarea
                value={histoireData.partie1.chapitre2.contenu}
                onChange={(e) => handleInputChange(e, 'partie1', 'chapitre2.contenu')}
                rows="4"
                cols="50"
              />
            </div>
          </div>

          <div className="navigation">
            <button type="button" onClick={handleSave}>Sauvegarder</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Histoire;
