import React from "react";
import { useSecret } from './SecretContext';

const Secrets = () => {
  const { secrets } = useSecret();

  return (
    <div className="secrets-container">
      <h1>Secrets Révélés</h1>
      <ul>
        {secrets.map((secret) => (
          <li key={secret.id} className={secret.found ? "revealed" : "hidden"}>
            <span className="secret-text">{secret.text}</span>
            <span className="secret-status">
              {secret.found ? "Révélé" : "Non Révélé"}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Secrets;
