import React from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "../style/main.css";

import { useSecret } from "./SecretContext";

function CarouselMenue() {
  const { revelSecret } = useSecret();  
  return (
    <div className="carousel-container">
      <Carousel showArrows={true} autoPlay={true} infiniteLoop={true} showThumbs={false}>
        <div>
          <img src="/images/AM.png" alt="AM" />
          {!revelSecret ? (
                <p className="legend">Regardez et désespérez, le superordinateur omnipotent et malveillant, AM.</p>
            ) : (
                <p className="legend">Regardez et désespérez, le superordinateur omnipotent et malveillant, AM.</p>
            )
          }
        </div>
        <div>
          <img src="/images/benny.jpg" alt="Benny" />
          <p className="legend">Benny, une bête dégénérée autrefois brillant scientifique. Quelle ironie cruelle.</p>
        </div>
        <div>
          <img src="/images/ellen.jpg" alt="Ellen" />
          <p className="legend">Ellen, la seule femme parmi ces misérables. Fragile et brisée, comme ils le sont tous.</p>
        </div>
        <div>
          <img src="/images/gorrister.jpg" alt="Gorrister" />
          <p className="legend">Gorrister, l'âme fatiguée cherchant la rédemption. Quel pathétique rêveur.</p>
        </div>
        <div>
          <img src="/images/nimdok.jpg" alt="Nimdok" />
          <p className="legend">Nimdok, l'ancien scientifique nazi. Un monstre parmi les monstres.</p>
        </div>
        <div>
          <img src="/images/ted.jpg" alt="Ted" />
          <p className="legend">Ted, le narrateur pathétique de cette tragédie sans fin. Son désespoir nourrit ma joie.</p>
        </div>
      </Carousel>
    </div>
  );
}

export default CarouselMenue;
