// Secret.js
import React from "react";
import { useSecret } from './SecretContext';

function Secret() {
  const { secrets } = useSecret();

  if (!Array.isArray(secrets)) {
    // Vérifie si secrets est un tableau
    return <p>Erreur: Les secrets ne sont pas disponibles.</p>;
  }

  return (
    <div>
      <h1>Page des Secrets</h1>
      <p>Voici les secrets :</p>
      <ul>
        {secrets.map(secret => (
          <li key={secret.id}>
            <p>Secret : {secret.text}</p>
            <p>État du Secret : {secret.found ? "Révélé" : "Non Révélé"}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Secret;
