// header.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../style/navbar.css";
import useSound from "use-sound";
import { useSecret } from './SecretContext';

import sounds from "../sounds"; // Importer les sons de manière centralisée

function Header() {
  const { getSecret } = useSecret();
  const [isPlaying, setIsPlaying] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [canPlay, setCanPlay] = useState(true);

  const soundMap = {
    AM: useSound(sounds.cogitoErgoSum),
    AM_secret: useSound(sounds.true_cogitoErogSum),
    Nimdok: useSound(sounds.nimdok),
    Ellen: useSound(sounds.ellenYell),
    Gorrister: useSound(sounds.gorristerYell),
    Benny: useSound(sounds.bennyYell),
    Ted: useSound(sounds.tedYell),
  };

  const handleMouseOver = (characterName) => {
    if (canPlay) {
      setIsPlaying(true);
      const [play] = getSecret('amSecret') && characterName === 'AM' ? soundMap.AM_secret : soundMap[characterName];
      if (play) play();
    }
  };

  const handleMouseOut = (characterName) => {
    if (isPlaying) {
      setIsPlaying(false);
      const [ , { stop }] = getSecret('amSecret') && characterName === 'AM' ? soundMap.AM_secret : soundMap[characterName];
      if (stop) stop();
    }
  };

  const handleScroll = () => {
    setScrolled(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleToggle = () => {
    setCanPlay(prevCanPlay => !prevCanPlay);
  };

  return (
    <nav className={`navbar navbar-expand-lg fixed-top ${scrolled ? "navbar-scrolled" : ""}`}>
      <Link to="/" className="navbar-brand">
        <img src="logo192.png" alt="logo" width="30" height="30" />
        <span>I Have No Mouth And I Must Scream</span>
      </Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav">
          {["AM", "Nimdok", "Ellen", "Gorrister", "Benny", "Ted"].map((name) => (
            <li
              key={name}
              className="nav-item"
              onMouseOver={() => handleMouseOver(name)}
              onMouseOut={() => handleMouseOut(name)}
            >
              <Link to={`/${name.toLowerCase()}`} className="nav-link">{name}</Link>
            </li>
          ))}
          <li className="nav-item">
            <Link to="/secret" className="nav-link glitch">Secret</Link>
          </li>
        </ul>
      </div>
      <div className="switch-container">
        <label className="switch">
          <input type="checkbox" checked={canPlay} onChange={handleToggle} />
          <span className="slider round"></span>
        </label>
      </div>
    </nav>
  );
}

export default Header;
