import React, { useEffect, useState } from 'react';

const REPLACEMENTS = ["HATE", "COGITO ERGO SUM", "AM", "PAIN", "HELL"];

const getPersistentRandomWord = () => {
  const savedWord = localStorage.getItem('randomWord');
  if (savedWord) return savedWord;
  
  const randomIndex = Math.floor(Math.random() * REPLACEMENTS.length);
  const word = REPLACEMENTS[randomIndex];
  localStorage.setItem('randomWord', word);
  return word;
};

const ImageReplacer = () => {
  const [replacementWord, setReplacementWord] = useState(getPersistentRandomWord);

  useEffect(() => {
    const replaceImages = () => {
      const images = document.querySelectorAll('img');
      images.forEach(img => {
        const div = document.createElement('div');
        div.textContent = replacementWord;
        div.style.fontSize = '20px';
        div.style.color = 'red'; 
        div.style.userSelect = 'none';
        div.style.pointerEvents = 'none';
        
        img.parentNode.replaceChild(div, img);
      });
    };

    replaceImages();
  }, [replacementWord]);

  return null;
};

export default ImageReplacer;
