import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { revealSecret, setRevealSecret } = useSecret(); // Corrected typo in variable name

  useEffect(() => {
    // Fetch character data from JSON server
    fetch('http://localhost:5000/api/character') // Adjust URL based on your API endpoint
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching character data:', error);
        setLoading(false);
      });
  }, [name]);

  // Handle mouse over event to reveal a specific letter for each character
  const handleMouseOver = () => {
    switch (name) {
      case "Ted":
        setRevealedLetter('H');
        break;
      case "Ellen":
        setRevealedLetter('A');
        break;
      case "Nimdok":
        setRevealedLetter('T');
        break;
      case "Benny":
        setRevealedLetter('E');
        break;
      case "Gorrister":
        setRevealedLetter('S');
        break;
      default:
        setRevealedLetter('');
        break;
    }
  };

  // Handle mouse out event to reset revealed letter
  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  // Handle key down event for AM character to input a specific sequence
  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();
    
    // Check if the character is AM before proceeding
    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      // Update displayedSequence if the key matches the next letter in "HATES" sequence
      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        // Reset displayedSequence if the key does not match the expected next letter
        setDisplayedSequence('');
      }
    }
  };

  // Effect to play AM's reason audio when sequence is completed
  useEffect(() => {
    if (displayedSequence === hateSequence) {
      audioRef.current.play();
      setRevealSecret(true); // Trigger secret reveal when sequence is completed
    }
  }, [displayedSequence]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!character) {
    return <div>Character not found</div>;
  }

  return (
    <div className="character-details">
      <h2>{character.name}</h2>
      <div className="description">
        <h3>Description</h3>
        <p>{character.book.description}</p>
      </div>
      <div className="detail">
        <h3>Background</h3>
        <p>{character.book.detail.background.story}</p>
      </div>
      <div className="relationships">
        <h3>Relationships</h3>
        <ul>
          {Object.entries(character.book.detail.relationships).map(([key, value]) => (
            <li key={key}><strong>{key}:</strong> {value}</li>
          ))}
        </ul>
      </div>
      <div className="tortures">
        <h3>Tortures</h3>
        <p>{character.book.detail.tortures.description}</p>
      </div>
      <div className="end">
        <h3>End</h3>
        <p>{character.book.detail.end.description}</p>
      </div>
      {/* Conditionally render audio and secret reveal button for AM */}
      {character.name === "AM" && (
        <div className="am-sequence">
          <audio ref={audioRef} src={amReason} />
          <p>Type "{hateSequence}" to reveal the secret...</p>
          <p>Typed sequence: {typedSequence}</p>
          <p>Displayed sequence: {displayedSequence}</p>
        </div>
      )}
      <div className="hover-reveal" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
        <p>Hover to reveal: {revealedLetter}</p>
      </div>
    </div>
  );
}

export default Character;
