import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useSecret } from './SecretContext';
import sounds from "./sounds";
import { useStore } from './store';
import { FiMenu } from 'react-icons/fi';
import ReactSwitch from 'react-switch';
import '../style/index.css';

function Header() {
  const { getSecret } = useSecret();
  const { isPlaying, scrolled, canPlay, togglePlay, setScrolled } = useStore();
  
  const handleScroll = () => {
    setScrolled(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [setScrolled]);

  const playSound = (characterName) => {
    if (canPlay) {
      const soundFile = getSecret('amSecret') && characterName === 'AM' ? sounds.true_cogitoErogSum : sounds[`${characterName.toLowerCase()}Yell`];
      if (soundFile) {
        const audio = new Audio(soundFile);
        audio.play();
      }
    }
  };

  return (
    <nav className={`navbar ${scrolled ? "navbar-scrolled" : ""} bg-gray-900 border-b-4 border-red-800 shadow-lg text-white`}>
      <div className="container mx-auto px-4 flex justify-between items-center">
        <Link className="navbar-brand flex items-center" to="/">
          <img src={`${process.env.PUBLIC_URL}/favico.ico`} alt="" width="30" height="24" className="mr-2" />
          IHNMAIMS
        </Link>
        <div className="flex items-center">
          <div className="hidden md:flex space-x-4">
            <Link className="nav-link hover:text-red-500" to="/">Accueil</Link>
            <Link className="nav-link hover:text-red-500" to="/story">Histoire</Link>
            <div className="relative group">
              <button className="nav-link flex items-center space-x-1">
                <span>Personnages</span>
                <svg className="w-4 h-4 mt-1 group-hover:text-red-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                </svg>
              </button>
              <div className="absolute left-0 mt-1 w-48 bg-gray-800 border border-gray-700 rounded shadow-lg hidden group-hover:block">
                <Link to="/character/AM" className="dropdown-item" onMouseOver={() => playSound("AM")}>
                  A.M
                </Link>
                <hr className="border-t border-gray-700 my-1" />
                <CharacterLink name="Ted" playSound={playSound} />
                <CharacterLink name="Ellen" playSound={playSound} />
                <CharacterLink name="Nimdok" playSound={playSound} />
                <CharacterLink name="Benny" playSound={playSound} />
                <CharacterLink name="Gorrister" playSound={playSound} />
              </div>
            </div>
            {getSecret("pageSecret") && (
              <Link className="nav-link-glitch hover:text-red-500" to="/Secret">Secret</Link>
            )}
          </div>
          <ReactSwitch
            checked={canPlay}
            onChange={togglePlay}
            onColor="#ff4500"
            offColor="#0a0a0a"
            onHandleColor="#ffffff"
            offHandleColor="#ffffff"
            handleDiameter={30}
            uncheckedIcon={false}
            checkedIcon={false}
            height={20}
            width={48}
            className="ml-4"
          />
          <button className="md:hidden text-white">
            <FiMenu className="w-6 h-6" />
          </button>
        </div>
      </div>
    </nav>
  );
}

const CharacterLink = React.memo(({ name, playSound }) => (
  <Link
    to={`/character/${name}`}
    className="dropdown-item"
    onMouseOver={() => playSound(name)}
  >
    {name}
  </Link>
));

export default Header;
