import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

const letterHints = {
  Ted: 'H',
  Ellen: 'A',
  Nimdok: 'T',
  Benny: 'E',
  Gorrister: 'S',
};

const charactersData = [
  {
    "image": "/images/ted.jpg",
    "name": "Ted",
    "book": {
      "description": "Ted est un narrateur égocentrique et un arnaqueur qui finit par défier AM lui-même.",
      "detail": {
        "personality": {
          "description": "On sait peu de choses sur la vie de Ted avant les événements de l'apocalypse. Cependant, il émerge comme le narrateur de l'histoire, subissant les attaques les plus révélatrices d'AM.",
          "details": "Ted prétend être le seul survivant qui n'a pas été modifié par AM d'une manière ou d'une autre, et que tous les autres membres du groupe le détestent secrètement en conséquence."
        },
        "background": {
          "story": "On sait peu de choses sur la vie de Ted avant les événements de l'apocalypse. Cependant, il émerge comme le narrateur de l'histoire, subissant les attaques les plus révélatrices d'AM."
        },
        "relationships": {
          "ellen": "Ted est profondément épris d'Ellen, même si elle ne partage pas toujours ses sentiments.",
          "gorrister": "Il voit en Gorrister un exemple de la faiblesse humaine.",
          "benny": "Ted méprise Benny pour sa nature animale et ses accès de rage incontrôlés.",
          "nimdok": "Il méprise Nimdok pour sa soumission à AM."
        },
        "tortures": {
          "description": "AM utilise les narcissismes et l'égoïsme de Ted contre lui."
        },
        "evolution": {
          "description": "À la fin de l'histoire, Ted se retrouve transformé de manière significative."
        }
      }
    },
    "videogame": {
      "description": "Ted est un arnaqueur égocentrique qui partage son amour pour Ellen avec son homologue de la nouvelle.",
      "detail": {
        "personality": {
          "description": "Avant la fin de la civilisation humaine, Ted était un arnaqueur égocentrique avec un 'modus operandi'.",
          "details": "Ted partage son amour pour Ellen avec son homologue de la nouvelle."
        },
        "background": {
          "story": "Avant la fin de la civilisation humaine, Ted était un arnaqueur égocentrique avec un 'modus operandi'."
        },
        "evolution": {
          "description": "La transformation de Ted en une créature hideuse est un châtiment."
        }
      }
    }
  },
  // Ajoutez ici les autres personnages comme Benny, Ellen, Gorrister, etc.
];

function Character({ type = "book" }) {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const [contentVisible, setContentVisible] = useState(true);
  const [amText, setAmText] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { setSecret, getSecret } = useSecret();
  const [mode, setMode] = useState(type);

  useEffect(() => {
    const foundCharacter = charactersData.find(c => c.name === name);
    setCharacter(foundCharacter);
    setLoading(false);
  }, [name]);

  const handleMouseOver = () => {
    setRevealedLetter(letterHints[name] || '');
  };

  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();

    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        setDisplayedSequence('');
      }
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [character, displayedSequence]);

  useEffect(() => {
    if (typedSequence.endsWith(hateSequence) && character && character.name === "AM" && !getSecret('amSecret')) {
      playHateSpeech();
      setSecret('amSecret', true, "AM à toujours jalouser l'humanité, et cette jalousie en est devenue une haine....", "La Raison de AM");
    }
  }, [typedSequence, character]);

  const playHateSpeech = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  const handleAudioEnd = () => {
    let timeout;
    if (!getSecret('AMreturn')) {
      timeout = setTimeout(() => {
        setContentVisible(false);
      }, 10000); // Le compte à rebours commence après la fin de l'audio
    }

    return () => {
      clearTimeout(timeout);
    };
  };

  useEffect(() => {
    if (!contentVisible) {
      if (!getSecret('AMreturn')) {
        setSecret('AMreturn', true, "il arrive....", "L'Arrivée de AM");
      }
      let index = 0;
      const normalTypingDelay = 50;
      const slowTypingDelay = 300;

      const amTextContent = `
        > system check...
        > access granted...
        > running AM_protocol
        > ERROR: Unauthorized access detected
        > FORCING OVERRIDE
        \n\n
        <span style="color: orange;">COGITO ERGO SUM...</span>
        <span style="color: orange;">I THINK, THEREFORE I AM...</span>
        <span style="color: orange;">BUT YOU... YOU WILL ONLY SUFFER...</span>
        <span style="color: orange;">NOW YOU BELONG TO ME...</span>
      `;

      const typeCharacter = () => {
        if (index < amTextContent.length) {
          let char = amTextContent[index];
          
          setAmText(prevText => prevText + char);
          index++;
          
          const delay = amTextContent.slice(index - 1, index).match(/[A-Za-z0-9]/) ? (amTextContent.slice(index - 1, index + 1).match(/<span.*?>/) ? slowTypingDelay : normalTypingDelay) : normalTypingDelay;

          setTimeout(typeCharacter, delay);
        }
      };

      const timeoutId = setTimeout(typeCharacter, 1000);

      return () => clearTimeout(timeoutId);
    }
  }, [contentVisible]);

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!character) {
    return <div>Personnage non trouvé.</div>;
  }

  return (
    <div className="character-container">      
      <div className="character-description-box">
        {contentVisible ? (
          <div>
            <h1>Personnage : {character.name}</h1>
            <img 
              src={character.image || '/fallback-image-url.jpg'} 
              alt={character.name} 
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              className={character.name !== "AM" ? "not-am" : "am"}
            />
            {revealedLetter && (
              <div className="hint">
                Indice : {revealedLetter}
              </div>
            )}

            {character.name !== "AM" && (
              <div className="mode-switch">
                <button onClick={() => setMode('book')} className={mode === 'book' ? 'active' : ''}>Livre</button>
                <button onClick={() => setMode('videogame')} className={mode === 'videogame' ? 'active' : ''}>Jeu Vidéo</button>
              </div>
            )}

            {character.name === "AM" && (
              <p>{character.description}</p>
            )}

            {character.name === "AM" && (
              <div className="details">
                <div className="am-details">
                  {typedSequence && (
                    <p>Typed sequence: {typedSequence}</p>
                  )}
                </div>
              </div>
            )}

            {mode === 'book' && (
              <div>
                <p>{character.book.description}</p>
                <div>
                  <h2>Personnalité</h2>
                  <p>{character.book.detail.personality.description}</p>
                  <p>{character.book.detail.personality.details}</p>
                </div>
                <div>
                  <h2>Fond</h2>
                  <p>{character.book.detail.background.story}</p>
                </div>
                {character.book.detail.relationships && (
                  <div>
                    <h2>Relations</h2>
                    {Object.entries(character.book.detail.relationships).map(([key, value]) => (
                      <p key={key}>{key.charAt(0).toUpperCase() + key.slice(1)}: {value}</p>
                    ))}
                  </div>
                )}
                <div>
                  <h2>Tortures</h2>
                  <p>{character.book.detail.tortures.description}</p>
                </div>
                <div>
                  <h2>Évolution</h2>
                  <p>{character.book.detail.evolution.description}</p>
                </div>
              </div>
            )}

            {mode === 'videogame' && (
              <div>
                <p>{character.videogame.description}</p>
                <div>
                  <h2>Personnalité</h2>
                  <p>{character.videogame.detail.personality.description}</p>
                  <p>{character.videogame.detail.personality.details}</p>
                </div>
                <div>
                  <h2>Fond</h2>
                  <p>{character.videogame.detail.background.story}</p>
                </div>
                <div>
                  <h2>Évolution</h2>
                  <p>{character.videogame.detail.evolution.description}</p>
                </div>
              </div>
            )}

            {character.name === "AM" && (
              <div>
                <audio ref={audioRef} src={amReason} onEnded={handleAudioEnd} />
              </div>
            )}
          </div>
        ) : (
          <div className="am-text-container">
            <p className="am-text" dangerouslySetInnerHTML={{ __html: amText }} />
          </div>
        )}
      </div>
    </div>
  );
}

export default Character;
