import React, { createContext, useState, useContext, useEffect } from 'react';

const SecretContext = createContext();

export const SecretProvider = ({ children }) => {
  const [secrets, setSecrets] = useState({});
  const [descriptions, setDescriptions] = useState({});

  useEffect(() => {
    // Charger les secrets et descriptions depuis sessionStorage lors du premier rendu
    const savedSecrets = JSON.parse(sessionStorage.getItem('secrets')) || {};
    const savedDescriptions = JSON.parse(sessionStorage.getItem('descriptions')) || {};
    setSecrets(savedSecrets);
    setDescriptions(savedDescriptions);
  }, []);

  useEffect(() => {
    // Sauvegarder les secrets et descriptions dans sessionStorage à chaque changement
    sessionStorage.setItem('secrets', JSON.stringify(secrets));
    sessionStorage.setItem('descriptions', JSON.stringify(descriptions));
  }, [secrets, descriptions]);

  const setSecret = (key, value) => {
    setSecrets(prevSecrets => ({ ...prevSecrets, [key]: value }));
  };

  const getSecret = (key) => {
    return secrets[key];
  };

  const setDescription = (key, value) => {
    setDescriptions(prevDescription => ({ ...prevDescription, [key]: value }));
  };

  const getDescription = (key) => {
    return descriptions[key];
  }

  return (
    <SecretContext.Provider value={{ setSecret, getSecret, setDescription, getDescription }}>
      {children}
    </SecretContext.Provider>
  );
};

export const useSecret = () => {
  const context = useContext(SecretContext);
  if (!context) {
    throw new Error('useSecret must be used within a SecretProvider');
  }
  return context;
};
