// Secret.js
import React from "react";
import { useSecret } from './SecretContext';

function Secret() {
  const { secrets } = useSecret(); // Accède à la liste des secrets depuis le contexte

  return (
    <div>
      <h1>Page des Secrets</h1>
      <p>Voici les secrets :</p>
      <ul>
        {secrets.map(secret => (
          <li key={secret.id}>
            <p>Secret : {secret.text}</p>
            <p>État du Secret : {secret.found ? "Révélé" : "Non Révélé"}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Secret;
