import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { revelSecret, setRevelSecret } = useSecret();

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données des personnages:', error);
        setLoading(false);
      });
  }, [name]);

  const handleMouseOver = () => {
    // Révéler la lettre correspondante à chaque personnage selon la séquence "HATES"
    switch (name) {
      case "Ted":
        setRevealedLetter('H');
        break;
      case "Ellen":
        setRevealedLetter('A');
        break;
      case "Nimdok":
        setRevealedLetter('T');
        break;
      case "Benny":
        setRevealedLetter('E');
        break;
      case "Gorrister":
        setRevealedLetter('S');
        break;
      default:
        setRevealedLetter('');
        break;
    }
  };

  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();
    
    // Vérifier si le personnage est AM avant de continuer
    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      // Mettre à jour displayedSequence si la touche correspond à la prochaine lettre de la séquence "HATES"
      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        // Réinitialiser displayedSequence si la touche ne correspond pas à la prochaine lettre attendue
        setDisplayedSequence('');
      }
    }
  };

  return (
    <div className="character-details">
      {loading && <p>Chargement...</p>}
      {character && (
        <>
          <h2>{character.name}</h2>
          <div className="character-description">
            <p>{character.book.description}</p>
            <p>{character.videogame.description}</p>
          </div>
          <div className="character-details">
            <h3>Background</h3>
            <p>{character.book.detail.background.story}</p>
          </div>
          <div className="character-details">
            <h3>Personality</h3>
            <p>{character.book.detail.personality.description}</p>
          </div>
          <div className="character-details">
            <h3>Relationships</h3>
            <ul>
              {Object.keys(character.book.detail.relationships).map((key, index) => (
                <li key={index}><strong>{key}:</strong> {character.book.detail.relationships[key]}</li>
              ))}
            </ul>
          </div>
          <div className="character-details">
            <h3>Tortures</h3>
            <p>{character.book.detail.tortures.description}</p>
          </div>
          {character.book.detail.evolution && (
            <div className="character-details">
              <h3>Evolution</h3>
              <p>{character.book.detail.evolution.description}</p>
            </div>
          )}
          {character.book.detail.end && (
            <div className="character-details">
              <h3>End</h3>
              <p>{character.book.detail.end.description}</p>
            </div>
          )}
        </>
      )}
      <audio ref={audioRef} src={amReason} controls />
    </div>
  );
}

export default Character;
