import React, { useState, useEffect } from "react";
import '../style/main.css';
import '../style/histoire.css';

function Histoire() {
  const [histoire, setHistoire] = useState(null);
  const [currentSection, setCurrentSection] = useState(1);

  useEffect(() => {
    fetch('http://localhost:5000/api/histoire') // Assurez-vous que l'URL correspond à votre route Express pour l'histoire
      .then(response => response.json())
      .then(data => {
        setHistoire(data.histoire); // Assurez-vous que votre structure JSON est correctement définie
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données d\'histoire:', error);
      });
  }, []);

  const handleNextSection = () => {
    setCurrentSection(prevSection => prevSection + 1);
  };

  const handlePrevSection = () => {
    setCurrentSection(prevSection => prevSection - 1);
  };

  return (
    <div className="main-container">
      <div className="description-box">
        <h1>Histoire</h1>
        {histoire && (
          <>
            {/* Affichage de la section actuelle */}
            <section className="section">
              <h2>{histoire[`partie${currentSection}`].titre}</h2>
              <div className="sub-section">
                <h3>{histoire[`partie${currentSection}`].chapitre1.titre}</h3>
                <p>{histoire[`partie${currentSection}`].chapitre1.contenu}</p>
                {/* Jeu contenu */}
                <p className="italic">{histoire[`partie${currentSection}`].chapitre1.jeu_contenu}</p>
              </div>
              <div className="sub-section">
                <h3>{histoire[`partie${currentSection}`].chapitre2.titre}</h3>
                <p>{histoire[`partie${currentSection}`].chapitre2.contenu}</p>
                {/* Jeu contenu */}
                <p className="italic">{histoire[`partie${currentSection}`].chapitre2.jeu_contenu}</p>
              </div>
            </section>

            {/* Navigation */}
            <div className="navigation">
              <button onClick={handlePrevSection} disabled={currentSection === 1}>Précédent</button>
              {histoire && Array.from(Array(5).keys()).map((index) => (
                <button key={index+1} onClick={() => setCurrentSection(index+1)} className={currentSection === index+1 ? 'active' : ''}>
                  {index + 1}
                </button>
              ))}
              <button onClick={handleNextSection} disabled={currentSection === 5}>Suivant</button>
            </div>
                      </>
        )}
      </div>
    </div>
  );
}

export default Histoire;
