// SecretContext.js
import React, { createContext, useState, useContext } from 'react';

const SecretContext = createContext();

const initialSecrets = [
  { id: 1, text: 'La raison de la folie de AM', found: false },
];

export const SecretProvider = ({ children }) => {
  const [secrets, setSecrets] = useState(initialSecrets);

  const toggleSecret = (id) => {
    setSecrets((prevSecrets) =>
      prevSecrets.map((secret) =>
        secret.id === id ? { ...secret, found: !secret.found } : secret
      )
    );
  };

  return (
    <SecretContext.Provider value={{ secrets, toggleSecret }}>
      {children}
    </SecretContext.Provider>
  );
};

export const useSecret = () => {
  const context = useContext(SecretContext);
  if (!context) {
    throw new Error('useSecret must be used within a SecretProvider');
  }
  return context;
};
