import create from 'zustand';

export const useStore = create((set) => ({
  isPlaying: false,
  scrolled: false,
  canPlay: true,
  togglePlay: () => set((state) => ({ canPlay: !state.canPlay })),
  setScrolled: (scrolled) => set({ scrolled }),
}));
