// SecretContext.js
import React, { createContext, useState, useContext } from 'react';

// Création du contexte
const SecretContext = createContext();

// Fournisseur de contexte
export function SecretProvider({ children }) {
  const [secrets, setSecrets] = useState({
    AM: false,
    Nimdok: false,
    // Ajoutez d'autres secrets ici
  });

  const revelSecret = (name) => {
    setSecrets(prevSecrets => ({ ...prevSecrets, [name]: true }));
  };

  return (
    <SecretContext.Provider value={{ secrets, revelSecret }}>
      {children}
    </SecretContext.Provider>
  );
}

// Hook personnalisé pour utiliser le contexte
export function useSecret() {
  return useContext(SecretContext);
}
