import React, { createContext, useContext, useState } from 'react';
import useSound from 'use-sound';
import sounds from './sounds'; // Importer les sons

const SoundContext = createContext();

export const SoundProvider = ({ children }) => {
  const [isPlaying, setIsPlaying] = useState(false);

  // Hook personnalisé pour gérer les sons
  const useSoundEffect = (soundName) => {
    const [play, { stop }] = useSound(sounds[soundName]);
    return [play, stop];
  };

  // Fonctions pour jouer et arrêter les sons
  const playSound = (soundName) => {
    const [play] = useSoundEffect(soundName);
    if (play) play();
  };

  const stopSound = () => {
    setIsPlaying(false);
    // Il est nécessaire de gérer comment vous arrêtez le son ici
  };

  return (
    <SoundContext.Provider value={{ playSound, stopSound }}>
      {children}
    </SoundContext.Provider>
  );
};

export const useSoundContext = () => useContext(SoundContext);
