import React from 'react';
import { useSecret } from './SecretContext';
import './Secret.css'; // Assurez-vous que le chemin est correct

function Secret() {
  const { secrets } = useSecret();

  // Liste des secrets avec leurs indices
  const secretList = [
    {
      name: "AM",
      hint: "Les 5 héros savent un fragment de la raison de AM mais seul lui peut réunir ces 5 fragments...",
      description: "La raison de AM est plus complexe que ce que les héros imaginent. C'est une quête pour comprendre le véritable but d'AM."
    },
  ];

  return (
    <div className="secret-container">
      <h1>Secrets Révélés</h1>
      <ul>
        {secretList.map((secret) => (
          <li key={secret.name}>
            <h2>{secret.name}</h2>
            <p><strong>Indice:</strong> {secret.hint}</p>
            {secrets[secret.name] ? (
              <div>
                <p><strong>Description:</strong> {secret.description}</p>
              </div>
            ) : (
              <p><strong>Description:</strong> (Disponible après révélation)</p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Secret;
