import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Navbar, Nav, NavDropdown, Form, FormCheck } from "react-bootstrap";
import { FaVolumeUp, FaVolumeMute, FaCog } from "react-icons/fa";
import { motion } from "framer-motion";
import { useSecret } from './SecretContext';
import useSound from "use-sound";
import sounds from "./sounds";
import "../style/navbar.css";

const Header = () => {
  const { getSecret } = useSecret();
  const [scrolled, setScrolled] = useState(false);
  const [canPlay, setCanPlay] = useState(true);

  const [playCogitoErgoSum] = useSound(sounds.cogitoErgoSum);
  const [playTrueCogitoErgoSum] = useSound(sounds.true_cogitoErogSum);
  const [playNimdok] = useSound(sounds.nimdok);
  const [playEllenYell] = useSound(sounds.ellenYell);
  const [playGorristerYell] = useSound(sounds.gorristerYell);
  const [playBennyYell] = useSound(sounds.bennyYell);
  const [playTedYell] = useSound(sounds.tedYell);

  const handleMouseOver = (characterName) => {
    if (canPlay) {
      switch (characterName) {
        case 'AM':
          if (getSecret('amSecret')) {
            playTrueCogitoErgoSum();
          } else {
            playCogitoErgoSum();
          }
          break;
        case 'Nimdok':
          playNimdok();
          break;
        case 'Ellen':
          playEllenYell();
          break;
        case 'Gorrister':
          playGorristerYell();
          break;
        case 'Benny':
          playBennyYell();
          break;
        case 'Ted':
          playTedYell();
          break;
        default:
          break;
      }
    }
  };

  const handleScroll = () => {
    setScrolled(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Navbar
      bg={scrolled ? "dark" : "transparent"}
      variant="dark"
      expand="lg"
      className={`navbar ${scrolled ? "navbar-scrolled" : ""}`}
    >
      <Navbar.Brand as={Link} to="/">
        <img src={`${process.env.PUBLIC_URL}/favico.ico`} alt="Logo" width="30" height="24" />
        IHNMAIMS
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" />
      <Navbar.Collapse id="navbarScroll">
        <Nav className="mr-auto">
          <Nav.Link as={Link} to="/">Accueil</Nav.Link>
          <Nav.Link as={Link} to="/story">Histoire</Nav.Link>
          <NavDropdown title="Personnages" id="navbarScrollingDropdown">
            <NavDropdown.Item as={Link} to="/character/AM" onMouseOver={() => handleMouseOver("AM")} onMouseOut={() => handleMouseOut("AM")}>A.M</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Header>Protagonistes</NavDropdown.Header>
            <CharacterLink name="Ted" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
            <CharacterLink name="Ellen" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
            <CharacterLink name="Nimdok" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
            <CharacterLink name="Benny" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
            <CharacterLink name="Gorrister" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} />
          </NavDropdown>
        </Nav>
        <Form inline>
          <Form.Check
            type="switch"
            id="custom-switch"
            label={canPlay ? <FaVolumeUp /> : <FaVolumeMute />}
            checked={canPlay}
            onChange={() => setCanPlay(prevCanPlay => !prevCanPlay)}
          />
        </Form>
        {getSecret("pageSecret") && (
          <Nav.Link as={Link} to="/Secret" className="nav-link-glitch">Secret</Nav.Link>
        )}
      </Navbar.Collapse>
    </Navbar>
  );
};

const CharacterLink = ({ name, onMouseOver, onMouseOut }) => (
  <NavDropdown.Item
    as={Link}
    to={`/character/${name}`}
    onMouseOver={() => onMouseOver(name)}
    onMouseOut={onMouseOut}
  >
    {name}
  </NavDropdown.Item>
);

export default Header;
