import React from 'react';
import { useSecret } from './SecretContext';

const Secret = ({ key }) => {
  const { getSecret, getDescription } = useSecret();
  
  const secret = getSecret(key);
  const description = getDescription(key);
  
  return (
    <div>
      {secret !== undefined ? (
        <div>
          <p><strong>Description :</strong> {description || 'Aucune description disponible.'}</p>
          <p><strong>Révélé :</strong> {secret ? 'Oui' : 'Non'}</p>
        </div>
      ) : (
        <p>Pas de secret disponible pour cette clé.</p>
      )}
    </div>
  );
};

export default Secret;
