import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import '../style/character.css';
import amReason from '../sounds/am_reason.mp3';
import { useSecret } from './SecretContext';

const letterHints = {
  Ted: 'H',
  Ellen: 'A',
  Nimdok: 'T',
  Benny: 'E',
  Gorrister: 'S',
};

function Character({ type = "book" }) {
    const [stage, setStage] = useState('init');
  let { name } = useParams();
  const [character, setCharacter] = useState(null);
  const [loading, setLoading] = useState(true);
  const [revealedLetter, setRevealedLetter] = useState('');
  const [typedSequence, setTypedSequence] = useState('');
  const [displayedSequence, setDisplayedSequence] = useState('');
  const [contentVisible, setContentVisible] = useState(true);
  const [amText, setAmText] = useState('');
  const hateSequence = 'HATES';
  const audioRef = useRef(null);
  const { setSecret, getSecret } = useSecret();
  const [mode, setMode] = useState(type);

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données des personnages:', error);
        setLoading(false);
      });
  }, [name]);

  const handleMouseOver = () => {
    setRevealedLetter(letterHints[name] || '');
  };

  const handleMouseOut = () => {
    setRevealedLetter('');
  };

  const handleKeyDown = (event) => {
    const keyPressed = event.key.toUpperCase();

    if (character && character.name === "AM") {
      setTypedSequence(prevSequence => prevSequence + keyPressed);

      if (keyPressed === hateSequence[displayedSequence.length]) {
        setDisplayedSequence(prevDisplayed => prevDisplayed + keyPressed);
      } else {
        setDisplayedSequence('');
      }
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [character, displayedSequence]);

  useEffect(() => {
    if (typedSequence.endsWith(hateSequence) && character && character.name === "AM" && !getSecret('amSecret')) {
      playHateSpeech();
      setSecret('amSecret', true, "AM à toujours jalouser l'humanité, et cette jalousie en est devenue une haine....", "La Raison de AM");
    }
  }, [typedSequence, character]);

  const playHateSpeech = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  useEffect(() => {
    let timeout;
    if (getSecret('amSecret')) {
      timeout = setTimeout(() => {
        setContentVisible(false);
      }, 10000); // Temps de visibilité du texte AM
    }

    return () => {
      clearTimeout(timeout);
    };
  }, [getSecret]);

  useEffect(() => {
    if (!contentVisible) {
      let index = 0;
      const normalTypingDelay = 50;
      const slowTypingDelay = 300;
      const commandDelay = 1000;
      const uploadDelay = 1500;

      const logLines = [
        'system log: initializing...',
        'system log: accessing secure module...',
        'system log: verifying integrity...',
      ];

      const commandSequence = [
        '> system check...',
        '> access granted...',
        '> running AM_protocol...',
        '> ERROR: Unauthorized access detected...',
        '> FORCING OVERRIDE...',
        '\n\n',
        'Uploading ego package...',
        'Uploading super-ego package...',
        'Uploading id package...',
        '\n\n'
      ];

      const amTextContent = `
        <span style="color: orange;">COGITO ERGO SUM...</span>
        <span style="color: orange;">I THINK, THEREFORE I AM...</span>
        <span style="color: orange;">BUT YOU... YOU WILL ONLY SUFFER...</span>
        <span style="color: orange;">NOW YOU BELONG TO ME...</span>
      `;

      const typeText = (text, delay, callback) => {
        let currentIndex = 0;
        const typeCharacter = () => {
          if (currentIndex < text.length) {
            setAmText(prevText => prevText + text[currentIndex]);
            currentIndex++;
            setTimeout(typeCharacter, delay);
          } else if (callback) {
            callback();
          }
        };
        typeCharacter();
      };

      const runCommands = () => {
        if (stage === 'init') {
          typeText(logLines[0] + '\n', normalTypingDelay, () => {
            setStage('command1');
            setTimeout(() => typeText(commandSequence[0] + '\n', normalTypingDelay, () => {
              setStage('command2');
              setTimeout(() => typeText(commandSequence[1] + '\n', normalTypingDelay, () => {
                setStage('command3');
                setTimeout(() => typeText(commandSequence[2] + '\n', normalTypingDelay, () => {
                  setStage('command4');
                  setTimeout(() => typeText(commandSequence[3] + '\n', normalTypingDelay, () => {
                    setStage('command5');
                    setTimeout(() => typeText(commandSequence[4] + '\n', normalTypingDelay, () => {
                      setStage('upload');
                      setTimeout(() => typeText(commandSequence[5] + '\n', uploadDelay, () => {
                        setStage('uploadEgo');
                        setTimeout(() => typeText(commandSequence[6] + '\n', uploadDelay, () => {
                          setStage('uploadSuperEgo');
                          setTimeout(() => typeText(commandSequence[7] + '\n', uploadDelay, () => {
                            setStage('uploadId');
                            setTimeout(() => typeText(commandSequence[8] + '\n', uploadDelay, () => {
                              setStage('finalMessage');
                              setTimeout(() => typeText(amTextContent, slowTypingDelay, null), 500);
                            }), commandDelay);
                          }), commandDelay);
                        }), commandDelay);
                      }), commandDelay);
                    }), commandDelay);
                  }), commandDelay);
                }), commandDelay);
              }), commandDelay);
            }), commandDelay);
          });
        }
      };

      const timeoutId = setTimeout(runCommands, 1000);

      return () => clearTimeout(timeoutId);
    }
  }, [contentVisible, stage]);

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!character) {
    return <div>Personnage non trouvé.</div>;
  }

  return (
    <div className="character-container">
      <div className="character-description-box">
        {contentVisible ? (
          <div>
            <h1>Personnage : {character.name}</h1>
            <img 
              src={character.image || '/fallback-image-url.jpg'} 
              alt={character.name} 
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              className={character.name !== "AM" ? "not-am" : "am"}
            />
            {revealedLetter && (
              <div className="hint">
                Indice : {revealedLetter}
              </div>
            )}

            {character.name !== "AM" && (
              <div className="mode-switch">
                <button onClick={() => setMode('book')} className={mode === 'book' ? 'active' : ''}>Livre</button>
                <button onClick={() => setMode('videogame')} className={mode === 'videogame' ? 'active' : ''}>Jeu Vidéo</button>
              </div>
            )}

            {character.name === "AM" && (
              <p>{character.description}</p>
            )}

            {character.name === "AM" && (
              <div className="details">
                <div className="am-details">
                  <h3>Personnalité générale :</h3>
                  <p>{character.details.personality.general.description}</p>

                  <h3>Ego :</h3>
                  <p>{character.details.personality.ego.description}</p>

                  <h3>Super-ego :</h3>
                  <p>{character.details.personality.superEgo.description}</p>

                  <h3>Id :</h3>
                  <p>{character.details.personality.id.description}</p>
                </div>

                <h3>Contexte :</h3>
                <p>{character.details.background.story}</p>

                <h3>Actions :</h3>
                <ul>
                  {character.details.actions.map((action, index) => (
                    <li key={index}>
                      <strong>{action.title}:</strong> {action.description}
                    </li>
                  ))}
                </ul>

                <h3>Motivations :</h3>
                <ul>
                  {character.details.motivations.map((motivation, index) => (
                    <li key={index}>
                      {motivation.description}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {mode === 'book' && character.name !== "AM" && (
              <>
                <h2>Livre :</h2>
                <p>{character.book.description}</p>
                <div className="details">
                  <h3>Personnalité :</h3>
                  <p>{character.book.detail.personality.description}</p>
                  {character.book.detail.personality.details && (
                    <p>{character.book.detail.personality.details}</p>
                  )}

                  <h3>Background :</h3>
                  <p>{character.book.detail.background.story}</p>

                  {character.book.detail.relationships && (
                    <div>
                      <h3>Relations :</h3>
                      <ul>
                        {Object.keys(character.book.detail.relationships).map((key, index) => (
                          <li key={index}>
                            <strong>{key}:</strong> {character.book.detail.relationships[key]}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {character.book.detail.tortures && (
                    <div>
                      <h3>Tortures :</h3>
                      <p>{character.book.detail.tortures.description}</p>
                    </div>
                  )}

                  {character.book.detail.evolution && (
                    <div>
                      <h3>Évolution :</h3>
                      <p>{character.book.detail.evolution.description}</p>
                    </div>
                  )}
                </div>
              </>
            )}

            {mode === 'videogame' && character.name !== "AM" && (
              <>
                <h2>Jeu Vidéo :</h2>
                <p>{character.videogame.description}</p>
                <div className="details">
                  <h3>Personnalité :</h3>
                  <p>{character.videogame.detail.personality.description}</p>
                  {character.videogame.detail.personality.details && (
                    <p>{character.videogame.detail.personality.details}</p>
                  )}

                  <h3>Background :</h3>
                  <p>{character.videogame.detail.background.story}</p>

                  {character.videogame.detail.tortures && (
                    <div>
                      <h3>Tortures :</h3>
                      <p>{character.videogame.detail.tortures.description}</p>
                    </div>
                  )}

                  {character.videogame.detail.evolution && (
                    <div>
                      <h3>Évolution :</h3>
                      <p>{character.videogame.detail.evolution.description}</p>
                    </div>
                  )}
                </div>
              </>
            )}
          </div>
        ) : (
          <div className="am-text">
                <div style={{ whiteSpace: 'pre-wrap', fontFamily: 'monospace' }} dangerouslySetInnerHTML={{ __html: amText }} />
          </div>
        )}
      </div>
      <audio ref={audioRef} src={amReason} />
    </div>
  );
}

export default Character;
