import React, { useState, useEffect } from "react";
import "../style/main.css";
import hateSpeech from "../ressource/AM_hate_speech.mp3";
import hateSpeech_2 from "../ressource/HateSpeech_2.mp3";
import CarouselMenue from "./Carousel";

import { useSecret } from "./SecretContext";

function Main() {
  const [data, setData] = useState({});
  const [isGlitching, setIsGlitching] = useState(false); // État pour contrôler le glitch
  const [glitchColor, setGlitchColor] = useState(false); // État pour alternance des couleurs de glitch
  const { revelSecret } = useSecret();

  useEffect(() => {
    console.log("Fetching data...");
    fetch("http://localhost:5000/api/Home")
      .then(response => response.json())
      .then(data => {
        console.log("Data fetched:", data);
        setData(data);
      })
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  useEffect(() => {
    // Activer le glitch lorsque le secret est révélé
    if (revelSecret) {
      setIsGlitching(true);
      const glitchInterval = setInterval(() => {
        setGlitchColor(prevState => !prevState); // Alterner l'état de couleur de glitch
      }, 200); // Intervalle pour alterner les couleurs (ajustez au besoin)

      // Désactiver le glitch après un certain temps (par exemple 3 secondes)
      const glitchTimeout = setTimeout(() => {
        setIsGlitching(false);
        clearInterval(glitchInterval); // Arrêter l'interval de glitch
      }, 3000); // Durée totale de glitch (ajustez au besoin)
      setIsGlitching(true);
      return () => {
        clearInterval(glitchInterval);
        clearTimeout(glitchTimeout);
      };
    }
  }, [revelSecret]);

  const quoteText = data.quote ? data.quote.text : "";
  const quoteAuthor = data.quote ? data.quote.author : "";

  return (
    <div className={`main-container ${isGlitching ? "glitch-effect" : ""}`}>
      <div className={`description-box ${isGlitching ? "glitch-effect" : ""} ${glitchColor ? "glitch-color-1" : "glitch-color-2"}`}>
        <div className="top-content">
          <div className="text-content">
            <h1>{data.title}</h1>
            <p>{data.description}</p>
          </div>
          <div className="cover-container" />
        </div>

        <div className="bottom-content">
          {quoteText && quoteAuthor && (
            <>
              <blockquote className={isGlitching ? "glitch-effect" : ""}>
                <span className="citation">{quoteText}</span>
                <br />
                — {quoteAuthor}
              </blockquote>
              <audio controls>
                {!revelSecret ? (
                  <source src={hateSpeech} type="audio/mp3" />
                ) : (
                  <source src={hateSpeech_2} type="audio/mp3" />
                )}
                Votre navigateur ne supporte pas l'élément audio.
              </audio>
            </>
          )}

          <div className={`characters ${isGlitching ? "glitch-effect" : ""}`}>
            <h2>Personnages principaux :</h2>
            <CarouselMenue />
          </div>

          <h2 className={isGlitching ? "glitch-effect" : ""}>Thèmes abordés :</h2>
          <ul className={isGlitching ? "glitch-effect" : ""}>
            {data.themes &&
              data.themes.map((theme, index) => (
                <li key={index}>{theme}</li>
              ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Main;
