import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function Character() {
  let { name } = useParams();
  const [character, setCharacter] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/character')
      .then(response => response.json())
      .then(data => {
        const foundCharacter = data.characters.find(c => c.name === name);
        setCharacter(foundCharacter);
      })
      .catch(error => console.error('Erreur lors de la récupération des données des personnages:', error));
  }, [name]);

  if (!character) {
    return <div>Chargement...</div>;
  }

  return (
    <div>
      <h1>Personnage : {character.name}</h1>
      <img src={character.image} alt={character.name} />
      <p>{character.description}</p>
      <p>{character.details}</p>
    </div>
  );
}

export default Character;
